Bash(node -e "
const fs = require\('fs'\);
// SVGをPNGの代わりに使うため、SVGをコピーして icon-192.png / icon-512.png の代替SVGを作成
// 実際のビルドはvite-plugin-pwaが処理する（開発時はSVGで代用）
const svg = fs.readFileSync\('public/icons/icon.svg', 'utf-8'\);
fs.writeFileSync\('public/icons/icon-192.png', svg\);
fs.writeFileSync\('public/icons/icon-512.png', svg\);
console.log\('icon placeholders created'\);
")
